package com.xunyi_ko.mynotes.leetcode;

import org.junit.Test;

/**
 * 给定一个字符串 s，你可以通过在字符串前面添加字符将其转换为回文串。 找到并返回可以用这种方式转换的最短回文串。
 * 
 * @author xunyi
 */
public class ShortestPalindrome {
    @Test
    public void testShortestPalindrome() {
//        String s = "";
        String s = "babbbabbaba";
        long start = System.currentTimeMillis();
        System.out.println(shortestPalindrome3(s));
        System.out.println(shortestPalindrome3(s).equals(shortestPalindrome1(s)));
        System.out.println(shortestPalindrome1(s));
        System.out.println(System.currentTimeMillis() - start);
    }

    // 内存消耗超过90%+， 耗时太长， 300+ms
    // 主要耗时在于判断是否是回文的方法上
    public String shortestPalindrome1(String s) {
        if (s.length() <= 1) {
            return s;
        }

        int length = 1;
        char c = s.charAt(0);
        for (int i = s.length() - 1; i > 0; i--) {
            // 1.耗时330ms左右
//            if(isPalinerome(s, i)) {
            // 2.提前判断了可不可能是回文，耗时减少到270ms左右
//            if(c == s.charAt(i) && isPalinerome(s, i)) {
            // 3.少判断第一位，几乎不变:(
            if (c == s.charAt(i) && isPalinerome(s, 1, i - 1)) {
                length = i + 1;
                break;
            }
        }

        StringBuilder sb = new StringBuilder();
        for (int i = s.length() - 1; i >= length; i--) {
            sb.append(s.charAt(i));
        }
        return sb.toString() + s;
    }

    // 用尝试的方式，在前面加字符，判断是否是回文
    // 更慢。。。
    public String shortestPalindrome2(String s) {
        if (s.length() <= 1) {
            return s;
        }
        StringBuilder sb = new StringBuilder();
        char first = s.charAt(0);
        for (int i = s.length() - 1; i > 0; i--) {
            if (s.charAt(i) == first) {
                if (isPalinerome(sb + s, 0, sb.length() + s.length() - 1)) {
                    break;
                }
            }
            sb.append(s.charAt(i));
        }
        return sb + s;
    }

    // KMP算法
    // 速度可以提到10ms+
    public String shortestPalindrome3(String s) {
        int n = s.length();
        String rev = new StringBuilder(s).reverse().toString();
        String newString = s + "#" + rev;
        
        int nNew = newString.length();
        // 除第一位外
        // 记录与原字符串相同的长度
        // 如 abdcabc
        // 记录 0,0,0,0,1,2,0
        // 这样后面就可以在遍历时，碰到对不上的时候，可以更快找到下一个需要比较的位置
        // 如 abdcabd， 可以得知已经对上了6个，接下来再从数组中取出2，得知接下来需要和第三位比较
        int[] f = new int[nNew];
        for(int i = 1; i < nNew; i++) {
            int t = f[i - 1];
            while(t > 0 && newString.charAt(i) != newString.charAt(t)) {
                t = f[t - 1];
            }
            if(newString.charAt(i) == newString.charAt(t)) {
                t++;
            }
            f[i] = t;
        }
        return rev.substring(0, n - f[nNew - 1]) + s;
    }

    private boolean isPalinerome(String s, int begin, int end) {
        for (; begin <= end; begin++, end--) {
            if (s.charAt(begin) != s.charAt(end)) {
                return false;
            }
        }
        return true;
    }
}
